<script setup lang="ts">
import { isVideoFile } from 'utils'

const props = defineProps<{
  urlList: string[]
}>()
const visible = defineModel<boolean>('visible', { required: true })
const url = defineModel<string>('url', { required: true })
const index = computed(() => props.urlList.indexOf(url.value))

const imgFallback = 'data:image/png;base64,'

function handleClose() {
  visible.value = false
}

function handlePrev() {
  if (index.value > 0) {
    url.value = props.urlList[index.value - 1]
  }
}

function handleNext() {
  if (index.value < props.urlList.length - 1) {
    url.value = props.urlList[index.value + 1]
  }
}
</script>

<template>
  <Transition name="el-fade-in-linear">
    <Teleport v-if="visible" to="body">
      <div class="fixed inset-0 z-50 flex items-center justify-center bg-black/50">
        <button class="hover:text-primary absolute right-12vw top-10 z-10 text-12" @click="handleClose">
          <div class="i-ant-design-close-circle-outlined text-white" />
        </button>
        <button class="hover:text-primary absolute left-18vw top-50% z-10 text-12 -translate-y-1/2" @click="handlePrev">
          <div class="i-ant-design-left-outlined text-white" />
        </button>

        <button class="hover:text-primary absolute right-18vw top-50% z-10 text-12 -translate-y-1/2" @click="handleNext">
          <div class="i-ant-design-right-outlined text-white" />
        </button>
        <template v-for="item in urlList" :key="item">
          <video v-if="isVideoFile(item)" class="pointer-events-auto size-150" :src="item" controls :class="{ 'important-hidden': url !== item }" />
          <AImage
            v-else class="pointer-events-auto size-150" :class="{ 'important-hidden': url !== item }"
            :src="item" fit="cover" :preview="false" :fallback="imgFallback"
          />
        </template>
        <div class="absolute bottom-10 left-50% z-10 text-6 text-white -translate-x-1/2">
          {{ index + 1 }}/{{ urlList.length }}
        </div>
      </div>
    </Teleport>
  </Transition>
</template>
